import data from "./data";

function App() {
  return (
    <div >
      <header >
        <a href="/">Dyl's Book Store</a>
      </header>
      <main>
        <h1>Featured Books</h1>
        <div className="books">
        {
          data.books.map(book => (<div className="book" key={book.name}>
            <a href={`/books/${book.name}`}>
            <img src={book.image} alt={book.name}/>
            </a>
            <div className="bookInfo">
              <a href={`/books/${book.name}`}>
                <p>{book.name}</p>
              </a>
              <p>by {book.author}</p>
              <button>Add to Cart</button>
            </div>
          </div>))
        }
        </div>
      </main>
    </div>
  );
}

export default App;
